mapper.select:
select * from ( select b.* , u.nick as userNick from board b join users u on b.userNo = u.userNo where b.boardClass = 1
and b.boardTitle like concat('%', #{keyword}, '%')
order by b.boardReg desc) as T1 limit #{skip},#{amount}

mapper.update:
update board 
set boardCnt = boardCnt + 1 
where boardNo = #{boardNo}

